import React from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const SinglePostView = ({posts}) => {
const { id } = useParams();

const [currentPost] = posts.filter(post => post._id === id)
// const [currentRecipient] = posts.filter(post => post.author.username === recipient) 
const {title, description, location, price, willDeliver, author, username} = currentPost;

    return (
        <div>
            <h3>{title}</h3>
            <p>Description: {description}</p>
            <p>Price: {price}</p>
            <p>Location: {location}</p>
            <p>Will Deliver {willDeliver}</p>
            <Link to='/Posts' ><button>Back</button></Link>
            <Link to='/Message'>
                <button onClick={() => {window.localStorage.setItem('ID', id ), window.localStorage.setItem('Recipient', {username})}}>Send Message</button>
            </Link>
        </div>
    )
}

export default SinglePostView
