import React, {Fragment} from 'react';
import { Link } from 'react-router-dom';
import { deletePost, getPosts } from '../api';

const Posts = ({ posts }) => {
    const token = window.localStorage.getItem('token')

    const SearchBar = () => {
        const [posts, setPosts] = useState([]);
        const [searchTerm, setSearchTerm] = useState('');

        async function fetchPosts() {
            const results = await getPosts(token)
            setPosts(results.data.posts);
        }

        fetchPosts()

        function postMatches(post, text) {
            if (post.title.includes(text) || post.description.includes(text) || post.price.includes(text) || post.location.includes(text)) {
                return true
            }
            else {
                return false
            }
        }
        const filteredPosts = posts.filter(post => postMatches(post, searchTerm));
        const postsToDisplay = searchTerm.length ? filteredPosts : posts;

        return (
            <form onSubmit={ (event) => {
                event.preventDefault();
                return(
                    postsToDisplay.map(posts)
                )

              }}><h1>Search</h1>
              <input
               type='text'
               placeholder='Search'
               onChange={(event) => setSearchTerm(event.target.value)}
               />
               </form>
        )
    }

    return (
        <div>
        {
            posts.map((post) => {
                const {description, location, price, title, _id, isAuthor} = post;
                return (
                    <div key={_id}>
                    <h3>{title}</h3>
                    <p><strong>Description:</strong> {description}</p>
                    <p><strong>Price:</strong> {price}</p>
                    <p><strong>Location:</strong> {location}</p>
                    {
                        isAuthor ? (
                            <div>
                                <Link to={`/posts/edit-post/${_id}`}>
                            <button>Edit</button>
                                </Link>
                            <button onClick={() => {deletePost(token, _id)}}>Delete</button>
                            </div>
                        ) : (
                            <Link to={`/posts/${_id}`}>
                            <button onClick={() => {window.localStorage.setItem('thisPost', post.author.username), console.log(post.author.username)}}>
                                View
                            </button>
                            </Link>
                        )
                    }
                    </div>
                )
            })
        }
        </div>
)
}

export default Posts
